mapper.insert:
insert into item_order (id, user_id, store_id,
  order_no, total_price, quantity,
  status, created, updated
  )
values (#{id,jdbcType=INTEGER}, #{userId,jdbcType=INTEGER}, #{storeId,jdbcType=INTEGER},
  #{orderNo,jdbcType=BIGINT}, #{totalPrice,jdbcType=DECIMAL}, #{quantity,jdbcType=INTEGER},
  #{status,jdbcType=TINYINT}, #{created,jdbcType=TIMESTAMP}, #{updated,jdbcType=TIMESTAMP}
  )

mapper.select:
SELECT COUNT(0) FROM item_order WHERE status = 2
AND created >=#{startTime} AND created <=#{endTime}
UNION ALL
SELECT COUNT(0) FROM item_order WHERE status = 3
AND created >=#{startTime} AND created <=#{endTime}

SELECT a.id, a.user_id, a.order_no as orderNo, a.total_price as totalPrice, a.`quantity`, a.`status`, a.created, a.updated, a.admin_id, b.nickname, b.phone, c.pay_platform as payWay, d.username FROM `item_order` a LEFT JOIN app_user b on a.user_id = b.id LEFT JOIN payment_info c on c.order_no = a.order_no LEFT JOIN sys_admin d on d.id = a.admin_id
and a.status < 4
and b.nickname like concat('%',#{nickname},'%')
and b.phone = #{phone}
and a.order_no = #{orderNo}
and a.status = #{status}
and c.pay_platform = #{payWay}
and a.admin_id = #{adminId}
order by a.id DESC
