mapper.select:
select a.* from ( select id, title, summary, recharge_money, send_money, target_id, admin_id, type, status, created, null as targetName from member_event where type = 1 or type = 2 union all select a.id, a.title, a.summary, a.recharge_money, a.send_money, a.target_id, a.admin_id, a.type, a.status, a.created, b.title as targetName from member_event a left join item_sku b on a.target_id = b.id where type = 3 union all select a.id, a.title, a.summary, a.recharge_money, a.send_money, a.target_id, a.admin_id, a.type, a.status, a.created, b.name as targetName from member_event a left join virtual_item b on a.target_id = b.id where type = 4 ) a where a.status = 1
and a.admin_id = #{adminId}
and a.type = #{type}
and a.title like concat('%',#{title},'%')
order by a.created DESC

