mapper.select:
select a.id, a.blag_money, a.turnover_money, a.store_id, a.created, a.event_id, c.title, b.membership_card_id, b.order_no, b.expense_money as totalMoney, c.card_no, d.nickname, d.phone, e.name as storeName, f.title as eventName, b.user_id from store_turnover a left join store_member_charge b on a.store_member_id = b.id left join membership_card c on b.membership_card_id = c.id left join app_user d on d.id = b.user_id left join store e on a.store_id = e.id left join member_event f on a.event_id = f.id where 1=1
and a.store_id = #{storeId}
and b.merchant_id = #{merchantId}
and a.event_id = #{eventId}
and c.title like concat('%',#{title},'%')
and d.phone like concat('%',#{phone},'%')
and e.name like concat('%',#{storeName},'%')
and unix_timestamp(a.created) >= #{startTime} and unix_timestamp(a.created) < #{endTime}
order by a.created DESC

select sum(a.blag_money) as totalBlagMoney, sum(a.turnover_money) as totalTurnoverMoney from store_turnover a left join store_member_charge b on a.store_member_id = b.id left join membership_card c on b.membership_card_id = c.id left join app_user d on d.id = b.user_id left join store e on a.store_id = e.id left join member_event f on a.event_id = f.id where 1=1
and a.store_id = #{storeId}
and b.merchant_id = #{merchantId}
and a.event_id = #{eventId}
and c.title like concat('%',#{title},'%')
and d.phone like concat('%',#{phone},'%')
and e.name like concat('%',#{storeName},'%')
and unix_timestamp(a.created) >= #{startTime} and unix_timestamp(a.created) < #{endTime}
order by a.created DESC
