mapper.select:
SELECT a.order_no as orderNo, a.id, a.user_id as userId, a.quantity, a.`name`, a.cover, a.summary, a.price, a.total_price as totalPrice, a.`status`, a.created, a.updated, b.nickname, b.phone, c.pay_platform as payWay FROM `virtual_item_order` a LEFT JOIN app_user b on a.user_id = b.id LEFT JOIN payment_info c on a.order_no = c.order_no
and a.status < 4
and b.nickname like concat('%',#{nickname},"%")
and b.phone = #{phone}
and a.order_no = #{orderNo}
and a.status = #{status}
and c.pay_platform = #{payWay}
order by a.id DESC

SELECT COUNT(0) FROM virtual_item_order WHERE status = 2
AND created >=#{startTime} AND created <=#{endTime}
UNION ALL
SELECT COUNT(0) FROM virtual_item_order WHERE status = 3
AND created >=#{startTime} AND created <=#{endTime}
